import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import * as yup from 'yup';
import { yupResolver } from "@hookform/resolvers/yup";
import { FormProvider, SubmitHandler, useForm } from 'react-hook-form';
import ISignUpFormData from './FormProps/ISignUpFormData';
import InputForm from './InputForm';

const validationSchema = yup.object().shape({
    firstName: yup.string().required("Enter your first name!"),
    lastName: yup.string().required("Enter your last name!"),
    username: yup.string().required("Enter username!"),
    email: yup.string().required("Enter email address!"),
    password: yup.string().required("Enter password!"),
    passwordConfirmation: yup.string()
        .oneOf([yup.ref("password"), ""], "Password must match!").required("Confirm password!")
});

function Copyright() {
    return (
      <Typography variant="body2" color="textSecondary" align="center">
        {'Copyright © '}
        <Link color="inherit" href="">
          ExpenseTracker.com
        </Link>{' '}
        {new Date().getFullYear()}
        {'.'}
      </Typography>
    );
  }

const useStyles = makeStyles((theme) => ({
    paper: {
      borderRadius: 12,
      backgroundColor:"white",
      padding: theme.spacing(5),
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center'
    },
    avatar: {
      margin: theme.spacing(1),
      backgroundColor: theme.palette.secondary.main,
    },
    form: {
      width: '100%', // Fix IE 11 issue.
      marginTop: theme.spacing(3),
    },
    submit: {
      margin: theme.spacing(3, 0, 2),
    },
  }));

const SignUpForm: React.FC = () => {

    const styles = useStyles();
    const methods = useForm({
        resolver: yupResolver(validationSchema)
    });
    const { handleSubmit, errors } = methods;
    const onSubmit = (formValues: ISignInFormData) => {
        props.signIn(formValues.login, formValues.password);
    };

    return(
        <FormProvider {...methods}>
            <DialogTitle id="form-dialog-title" className={styles.title}>
                Sign In
            </DialogTitle>
            <DialogContent>
            <DialogContentText className={styles.subtitle}>
                Please, fill fields to sign in
            </DialogContentText>
            <form>
                <InputForm 
                errorObj={errors}
                name="login" 
                label="Login(Username/Email)" 
                autoFocus={true} 
                required={true}
                />
                <InputForm 
                errorObj={errors}
                type="password"
                name="password" 
                label="Password" 
                required={true}
                />
            </form>
            <DialogActions className={styles.buttonContainer}>
                <Button 
                    variant="contained" 
                    color="primary"
                    onClick={handleSubmit(onSubmit)}
                    className={styles.button}
                    >
                    Sign In
                </Button>
                <Button 
                    variant="contained" 
                    color="primary"
                    onClick={props.handleClose}
                    className={styles.button}
                    >
                    Cancel
                </Button>
            </DialogActions>
            </DialogContent>
        </FormProvider>
    );
}

export default SignUpForm;
