import React, { useCallback, Component, useState } from 'react';
import { useForm } from 'react-hook-form';
import { Box, Button, Dialog, FormControl, FormGroup, DialogContent, DialogTitle, DialogContentText, TextField, DialogActions } from '@material-ui/core';
import {makeStyles} from '@material-ui/core/styles';
import SignInForm from '../Forms/SignInForm';

export interface BearerToken {
    accessToken: string;
}

const useStyles = makeStyles((theme: any) => ({
    title: {
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        alignSelf: "center"
    }
}));

const SignInButton:React.FC = () => {

    const styles = useStyles();

    const [open, setOpen] = useState(false);
    const [login, setlogin] = useState("");
    const [password, setPassword] = useState("");

    // constructor(props: ISignInButtonProps)
    // {
    //     super(props);

    //     this.state = {
    //         open: false,
    //         username: "",
    //         password: "" 
    //     }
        
    //     this.handleClickOpen = this.handleClickOpen.bind(this);
    //     this.handleClose = this.handleClose.bind(this);
    //     this.signIn = this.signIn.bind(this);
    //     this.handleChangeUsername = this.handleChangeUsername.bind(this);
    //     this.handleChangePassword = this.handleChangePassword.bind(this);
    // }

    // handleChangeUsername(value: string) : void
    // {
    //     this.setState(() => ({
    //         username: value
    //     }));
    // }

    // handleChangePassword(value: string) : void
    // {
    //     this.setState(() => ({
    //         password: value
    //     }));
    // }

    const handleChangeLogin = (newLogin: string) => {
        setlogin(newLogin);
    }

    const handleChangePassword = (newPassword: string) => {
        setPassword(newPassword);
    }

    // signIn() : void
    // {
    //     const xhr = new XMLHttpRequest();

    //     xhr.addEventListener('load', () => {
    //         console.log(xhr.response);
    //     });

    //     xhr.send(JSON.stringify({username: this.state.username, password: this.state.password}));
    //     console.log(xhr.response);
    //     this.handleClose();
    // }

    const signIn = () => {
        console.log(login);
        console.log(password);
        handleClose();
    }

    // handleClickOpen() : void
    // {
    //     console.log("open");
    //     this.setState(() => ({
    //         open: true
    //     }));
    // }

    const handleClickOpen = () => {
        console.log("open");
        setOpen(true);
    }

    // handleClose() : void
    // {
    //     setOpen(false);
    // }

    const handleClose = () => {
        setOpen(false);
    }
    
    return (
        <Box mr={3}>
            <Button color="inherit" variant="outlined" onClick={handleClickOpen}>
                Sign in
            </Button>
            <Dialog open={open} onClose={handleClose} arial-lablledby="form-dialog-title">
                <SignInForm handleClose={handleClose} signIn={signIn} />
            </Dialog>
        </Box>
    );
}

export default SignInButton;


