import User from '../../Data/Models/User/User';
import UserForUpdate from '../../Data/Models/User/UserForUpdate';
import API from '../Api';

export const GetUserData = (id: number) => {

    API.get("/People/" + id)
        .then(response => {
            const person = response.data;
            let userData: User = {
                id: person.Id,
                firstName: person.Name,
                lastName: person.Name
            }
            console.log(response);
            return userData;
        })
        .catch(error => {
            console.log(error);
        });

}

export const EditUser = (user: User) => {
    const userForUpdate: UserForUpdate = {
        firstName: user.firstName,
        lastName: user.lastName
    }

    API.put('/People/' + user.id, userForUpdate)
        .then(response => {
            console.log(response.data);
            console.log(response);

            return response;
        })
        .catch(error => {
            console.log(error);
        })
}

export const CreateUser = (user: UserForUpdate) => {

    API.post('/People', user)
        .then(response => {
            console.log(response.data);
            console.log(response);

            return response.data;
        })
        .catch(error => {
            console.log(error);
        });
}

export const DeleteUser = (id: number) => {

    API.delete('/People/' + id)
        .then(response => {
            console.log(response);
        })
        .catch(error => {
            console.log(error);
        });
}

export default {
    GetUserData,
    EditUser,
    CreateUser,
    DeleteUser
}
