import React, { useContext, useEffect } from 'react';
import {
    Route, Redirect
  } from "react-router-dom";
import LeftMenu from '../Components/LeftMenu/LeftMenu';
import HomePage from '../Components/Pages/HomePage';
import ProfilePage from '../Components/Pages/ProfilePage';
import UserContext from '../Context/UserContext';
import UserServices from '../Services/user.services/User.services';

  const AuthorizedRouter:React.FC = () => {
      useEffect(() => {
          let userData = useContext(UserContext).userData;
          
          const fetchAPI = async () => {
            let user = await UserServices.GetUserData(userData.userId);
            if(user != null)
            {
                userData = user;
            }
          };

          fetchAPI();
      }, []);

    return(
        <React.Fragment>
            <LeftMenu />
            <div style={{paddingLeft: 256}}>
                <Route exact path="/au/home" component={HomePage} />
                <Route exact path="/au/profile" component={ProfilePage} />
                <Route render={() => <Redirect to="/au/home" />} />
            </div>
        </React.Fragment>
    );
  }

  export default AuthorizedRouter;
