import { Pie, Doughnut } from 'react-chartjs-2';
import React, { useEffect, useState } from 'react';  

const state = {
  labels: ['January', 'February', 'March',
           'April', 'May'],
  datasets: [
    {
      label: 'Rainfall',
      backgroundColor: [
        '#B21F00',
        '#C9DE00',
        '#2FDE00',
        '#00A6B4',
        '#6800B4'
      ],
      hoverBackgroundColor: [
      '#501800',
      '#4B5000',
      '#175000',
      '#003350',
      '#35014F'
      ],
      data: [65, 59, 80, 81, 56]
    }
  ]
};

  const CircleDiagram:React.FC = () => {

    return(
      <React.Fragment>
        <div>
          <Pie data={state}
          options={{
            title:{
              display:true,
              text: "Average Rainfall per month",
              fontSize: 20
            },
            legend:{
              display:true,
              position:"right"
            }
          }}
          />

          <Doughnut data={state}
          options={{
            title:{
              display:true,
              text:'Average Rainfall per month',
              fontSize:20
            },
            legend:{
              display:true,
              position:'right'
            }
          }}
        />
        </div>
      </React.Fragment>
    );
  }

  export default CircleDiagram;
