import { Doughnut } from 'react-chartjs-2';
import React from 'react';  

const state = {
  labels: ['January', 'February', 'March',
           'April', 'May'],
  datasets: [
    {
      label: 'Rainfall',
      backgroundColor: [
        '#B21F00',
        '#C9DE00',
        '#2FDE00',
        '#00A6B4',
        '#6800B4'
      ],
      hoverBackgroundColor: [
      '#501800',
      '#4B5000',
      '#175000',
      '#003350',
      '#35014F'
      ],
      data: [65, 59, 80, 81, 56]
    }
  ]
};

interface DoughnutDiagramProps
{
  width?: number,
  state?: {
    labels: string[];
    datasets: {
        label?: string;
        backgroundColor: string[];
        hoverBackgroundColor: string[];
        data: number[];
    }[];
  },
  title?: {
    display:boolean,
    text: string
  },
  cutoutPercentage?: number
}

const DoughnutDiagram:React.FC<DoughnutDiagramProps> = (props: DoughnutDiagramProps) => {
  const display = props.title?.display || false;

    return(
        <div>
            <Doughnut data={state} width={props.width}
            options={{
              responsive: true,
              maintainAspectRatio: true,
                title:{
                    display:{display},
                    text:'Average Rainfall per month',
                    fontSize:20
                },
                legend:{
                    display:true,
                    position:'right'
                },
                cutoutPercentage: 65
            }}
            />
        </div>
    );
}

export default DoughnutDiagram;
