import { List, ListItem, ListItemIcon, ListItemText, makeStyles } from "@material-ui/core";
import React from "react";

interface SidebarItems
{
    items: item[]
}

interface item
{
    name: string,
    label: string,
    item?: item
}

const useStyles = makeStyles((theme) => ({
    sidebar:{
        marginTop: theme.spacing(8),
        maxWidth:240,
        border: "1px solid rgba(0, 0, 0, 0.1)"
    }
}));

const Sidebar: React.FC<SidebarItems> = ({items}) => {
    
    const styles = useStyles();

    return (
        <div className={styles.sidebar}>
        <List disablePadding dense>
            {items.map(({ label, name, item: subItems, ...rest }) => (
            <ListItem style={{ paddingLeft: 18 }} key={name} button {...rest}>
                <ListItemText>{label}</ListItemText>
                {Array.isArray(subItems) ? (
                <List disablePadding>
                    {subItems.map((subItem) => (
                    <ListItem key={subItem.name} button>
                        <ListItemText className="sidebar-item-text">
                        {subItem.label}
                        </ListItemText>
                    </ListItem>
                    ))}
                </List>
                ) : null}
            </ListItem>
            ))}
        </List>
        </div>
      );
}

export default Sidebar;
