import React from 'react';
import { useFormContext, Controller } from 'react-hook-form';
import { createStyles, TextField } from '@material-ui/core';

interface IInputFormData
{
    name: string,
    label: string,
    autoFocus?: boolean
}

const InputForm: React.FC<IInputFormData> = ({name, label, autoFocus = false}) => {
    const { control } = useFormContext();

    return(
        <Controller 
            as={TextField}
            margin="dense"
            autoFocus={autoFocus}
            name={name}
            control={control}
            defaultValue=""
            label={label}
            fullWidth={true}
        />
    )
}

export default InputForm;
