import React, { useCallback, Component, useState } from 'react';
import { useForm } from 'react-hook-form';
import { Box, Button, Dialog, FormControl, FormGroup, DialogContent, DialogTitle, DialogContentText, TextField, DialogActions } from '@material-ui/core';
import {makeStyles} from '@material-ui/core/styles';
import SignInForm from '../Forms/SignInForm';

export interface BearerToken {
    accessToken: string;
}


interface ISignInButtonState 
{
    open: boolean,
    username: string,
    password: string
}
interface ISignInButtonProps 
{
    classes: Record<"title", string>
}

const useStyles = makeStyles((theme: any) => ({
    title: {
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        alignSelf: "center"
    }
}));

class SignInButton extends Component<ISignInButtonProps, ISignInButtonState> {

    constructor(props: ISignInButtonProps)
    {
        super(props);

        this.state = {
            open: false,
            username: "",
            password: ""
        }
        
        this.handleClickOpen = this.handleClickOpen.bind(this);
        this.handleClose = this.handleClose.bind(this);
        this.signIn = this.signIn.bind(this);
        this.handleChangeUsername = this.handleChangeUsername.bind(this);
        this.handleChangePassword = this.handleChangePassword.bind(this);
    }

    handleChangeUsername(value: string) : void
    {
        this.setState(() => ({
            username: value
        }));
    }

    handleChangePassword(value: string) : void
    {
        this.setState(() => ({
            password: value
        }));
    }

    signIn() : void
    {
        const xhr = new XMLHttpRequest();

        xhr.addEventListener('load', () => {
            console.log(xhr.response);
        });

        xhr.send(JSON.stringify({username: this.state.username, password: this.state.password}));
        console.log(xhr.response);
        this.handleClose();
    }

    handleClickOpen() : void
    {
        console.log("open");
        this.setState(() => ({
            open: true
        }));
    }

    handleClose() : void
    {
        this.setState(() => ({
            open:false,
            username: "",
            password: ""
        }));
    }
    
    render() {
        return (
            <Box mr={3}>
                <Button color="inherit" variant="outlined" onClick={this.handleClickOpen}>
                    Sign in
                </Button>
                <Dialog open={this.state.open} onClose={this.handleClose} arial-lablledby="form-dialog-title">
                    <SignInForm handleClose={this.handleClose} />
                </Dialog>
            </Box>
        );
    };
}

